    public static class SpawningMob {
        long realTime;
        int ticks;
        EntityArmorStand stand;
        String name;
        boolean slow;

        SpawningMob(EntityArmorStand stand, String name, boolean slow) {
            this.realTime = System.currentTimeMillis();
            this.stand = stand;
            this.name = name;
            this.ticks = slow ? 79 : 39;
            this.slow = slow;
        }
    }

    public static class mobName {
        long realTime;
        int ticks = 0;
        EntityArmorStand stand;
        String name;
        boolean slow;

        mobName(EntityArmorStand stand, String name, boolean slow) {
            this.stand = stand;
            this.realTime = System.currentTimeMillis();
            this.name = name;
            this.slow = slow;
        }
    }

    @SubscribeEvent
    public void onEntityMove(S17LookMoveEvent event) {
        if (DungeonTracker.getPhase() != 1) return;

        S14PacketEntity.S17PacketEntityLookMove packet = event.getPacket();
        WorldClient world = Minecraft.getMinecraft().theWorld;

        Entity ent = packet.getEntity(world);

        if (ent instanceof EntityArmorStand) {
            EntityArmorStand stand = (EntityArmorStand) ent;

            int id = stand.getEntityId();
            ItemStack head = stand.getEquipmentInSlot(4);
            if (head != null && head.getItem() == Items.skull) {

                NBTTagCompound tag = head.getTagCompound();
                if (tag != null && tag.hasKey("SkullOwner")) {
                    NBTTagCompound skullOwner = tag.getCompoundTag("SkullOwner");
                    if (skullOwner.hasKey("Properties")) {
                        NBTTagCompound properties = skullOwner.getCompoundTag("Properties");
                        if (properties.hasKey("textures")) {
                            NBTTagList textures = properties.getTagList("textures", 10);
                            if (textures.tagCount() > 0) {
                                String value = textures.getCompoundTagAt(0).getString("Value");

                                if (bloodMobs.containsKey(value)) {
                                    if (!consume.contains(id)) {
                                        bm.add(new SpawningMob(stand, bloodMobs.get(value), slowSpawn));
                                        consume.add(id);
                                        expected.add(bloodMobs.get(value));
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    @SubscribeEvent
    public void onServerTick(S32ReceivedEvent event) {
        if (DungeonTracker.getPhase() == 1) {
            for (SpawningMob mob : bm) {
                mob.ticks--;
            }

            WorldClient world = Minecraft.getMinecraft().theWorld;
            if (world != null) {
                for (Entity e : world.getLoadedEntityList()) {
                    if (e instanceof EntityArmorStand) {
                        EntityArmorStand stand = (EntityArmorStand) e;
                        int id = stand.getEntityId();

                        String next = expected.peek();
                        if (next != null) {
                            if (EnumChatFormatting.getTextWithoutFormattingCodes(stand.getName()).contains(next)) {
                                SpawningMob mob = bm.peek();
                                boolean spawnSpeed = mob == null || mob.slow;
                                if (!names.containsKey(id)) {
                                    names.put(id, new mobName(stand, next, spawnSpeed));
                                }
                                expected.remove();
                                if (mobSpawn.getValue()) {
                                    if (mob != null) { // safety check even tho it shouldnt be needed
                                        ChatUtils.system(mob.name + " took " + (System.currentTimeMillis() - mob.realTime) + "ms to spawn"); // i love 4128ms spawn time
                                    }
                                }
                                bm.remove();
                            }
                        }
                    }
                }
            }

            if (bm.peek() != null && bm.peek().ticks < -3) {
                bm.remove(); // catch non spawned mobs?
                expected.remove();
            }

            // this probably good?
            Iterator<Map.Entry<Integer, mobName>> iteratorN = names.entrySet().iterator();
            while (iteratorN.hasNext()) {
                Map.Entry<Integer, mobName> entry = iteratorN.next();
                mobName mob = entry.getValue();
                if (EnumChatFormatting.getTextWithoutFormattingCodes(mob.stand.getName()).endsWith(" 0â¤")) {
                    if (mobKill.getValue() && !mob.slow) {
                        long killTime = System.currentTimeMillis() - mob.realTime;
                        ChatUtils.system(mob.name + " took " + (killTime) + "ms to kill (" + mob.ticks + " ticks)");
                        totalMobs++;
                        totalTicks += mob.ticks;
                        rt += killTime;
                    }
                    iteratorN.remove();
                }

                mob.ticks++;
            }
        }
    }

    @SubscribeEvent
    public void onChatPacket(S02ChatReceivedEvent event) {
        String msg = event.getUnformattedMessage();

        if (msg.equals("The BLOOD DOOR has been opened!")) {
            slowSpawn = true;
        }
        if (msg.equals("[BOSS] The Watcher: Let's see how you can handle this.")) {
            slowSpawn = false;
        }
        if (mobKill.getValue() && msg.equals("[BOSS] The Watcher: You have proven yourself. You may pass.") && totalMobs > 0) {
            ChatUtils.system("Average Mob Kill Time: " + (rt / totalMobs) + "ms (" + (totalTicks/totalMobs) + " ticks)");
        }
    }

    @SubscribeEvent
    public void onWorldLoad(WorldEvent.Load event) {
        slowSpawn = false;
        totalMobs = 0;
        totalTicks = 0;
        rt = 0;
        consume.clear();
        expected.clear();
        names.clear();
        bm.clear();
        spawning.clear();
    }

    @SubscribeEvent
    public void onRenderWorld(RenderWorldLastEvent event) {
        if (mobTimer.getValue() && DungeonTracker.getPhase() == 1) {
            for (SpawningMob mob : bm) {
                RenderShapeUtils.drawFloatingText(
                        (mob.ticks*50) + "ms", mob.stand.posX, mob.stand.posY + 1.3, mob.stand.posZ, 0.06f, event.partialTicks
                );
            }
        }
    }

